import { createContext, useReducer } from "react";

import { getAllCategory } from "../API/Category";
import { getOrders } from "../API/Order";

export const AppContext = createContext(null);

const reducer = (state, action) => {
  switch (action) {
    case "getAllCategory":
      return {
        ...state,
        loading: false,
      };
  }
};
