import btoa from "btoa";
import { backendurl, username, password } from "../backend";

export const addCategory = async (category) => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/addCategory/`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
    body: JSON.stringify(category),
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};

export const deleteCategory = async (cid1) => {
  let cid = {
    cid: `${cid1}`,
  };
  return fetch(`https://rishtpusht.pythonanywhere.com/api/deleteCategory/`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
    body: JSON.stringify({ cid: cid }),
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};

export const editCategory = async (category) => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/editCategory/`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
    body: JSON.stringify(category),
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};

export const getAllCategory = async () => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/getCategories`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};
