import btoa from "btoa";
import { backendurl, username, password } from "../backend";

export const addProduct = async (product) => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/addProduct/`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
    body: JSON.stringify(product),
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};

export const getAllCategory = async () => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/getCategories`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};

export const getAllProducts = async () => {
  return fetch(
    `https://rishtpusht.pythonanywhere.com/api/products/category/${id}/`,
    {
      method: "POST",
      headers: {
        Authorization: "Basic " + btoa(`${username}:${password}`),
      },
    }
  )
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};
