import { createContext, useReducer } from "react";

export const AppContext = createContext(null);

export const reducer = (state, action) => {
  switch (action.type) {
    case "callingAPI":
      return {
        ...state,
        loading: true,
      };
    case "getAllCategory":
      return {
        ...state,
        loading: false,
        categories: action.data,
      };

    case "getAllOrders":
      return {
        ...state,
        loading: false,
        orders: action.data,
      };
  }
};

export const initialState = {
  categories: [],
  orders: [],
  loading: false,
};
