import React, { useState } from "react";

//components
import Upload from "../Components/Upload";
import Categorycard from "../Components/Categorycard";

//image
import bgimg from "../Assets/Images/bgimage.png";

//API
import { getImage } from "../API/Category";

const Item = () => {
  const [File, setFile] = useState(null);
  const [Status, setStatus] = useState(false);
  const [EditStatus, setEditStatus] = useState(false);

  const [formdata, setFormData] = useState({
    cid: "",
    scid: "",
    name: "",
    image_url: "",
    price: "",
    in_stock: "",
    short_description: "",
    description: "",
  });

  const setNameValue = (e) => {
    setFormData({ ...formdata, name: e.target.value });
  };

  const setDescriptionValue = (e) => {
    setFormData({ ...formdata, description: e.target.value });
  };

  const setShortDescriptionValue = (e) => {
    setFormData({ ...formdata, short_description: e.target.value });
  };

  const setCategoryId = (id) => {
    setFormData({ ...formdata, cid: id });
  };

  const setSubCatId = (id) => {
    setFormData({ ...formdata, scid: id });
  };

  const setPriceForm = (e) => {
    setPrice({ ...formdata, price: e.target.value });
  };

  const setFileHandler = (e) => {
    setFile(e.target.files[0]);
  };

  const handleForm = (e) => {
    e.preventDefault();
    alert("Form Submitted");
  };

  if (File) {
    const image = new FormData();

    image.append("file", File);

    getImage(image).then((response) => {
      setFormData({
        ...formdata,
        image_url: response.url,
      });
      alert("Image Uploaded Successfully..");
    });

    setFile(null);
  }

  return (
    <div>
      <h1 className="bold-text">Add item</h1>
      <div className="add-item">
        <Upload
          add="Item name"
          des="Item Description"
          shortdes="Item Short Description"
          price="Enter price"
          cat="Select subcategory"
          subcat="Select Subcategory"
          vname={formdata.name}
          vprice={formdata.price}
          vdesc={formdata.description}
          vshortdes={formdata.short_description}
          setName={setNameValue}
          setDescription={setDescriptionValue}
          setShortDescription={setShortDescriptionValue}
          setCategoryId={setCategoryId}
          setSubCatId={setSubCatId}
          setPriceForm={setPriceForm}
          setFileHandler={setFileHandler}
          handleForm={handleForm}
        />
      </div>
      <div className="modify-delete" style={{ marginTop: "120px" }}>
        <h1 className="bold-text">MODIFY/DELETE ITEM</h1>
        <div className="row g-4 text-center">
          <div className="col-md-2">
            <Categorycard img={bgimg} name="KIDNEY CARE" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Item;
