import btoa from "btoa";
import backend_uri from "../backend";


export const getOrders = async () => {
  return fetch(`https://rishtpusht.pythonanywhere.com/api/allorders/`, {
    method: "POST",
    headers: {
      Authorization: "Basic " + btoa(`${username}:${password}`),
    },
  })
    .then((response) => {
      return response.json();
    })
    .catch((err) => {
      return err;
    });
};
