import React from "react";

import vector from "../Assets/Images/Vector.png";
import { AppContext } from "../Context/Context";

const Upload = ({
  setName,
  setDescription,
  setShortDescription,
  setFileHandler,
  add,

  handleForm,
  des,
  name,
  descrp,
  pname,
  shortdes,
  price,
  cat,
  subcat,
  vname,
  vprice,
  vdes,
  vshortdes,
}) => {
  const { state } = useContext(AppContext);
  const { Categories } = state;
  return (
    <div>
      <form action="" onSubmit={handleForm}>
        <div className="row align-items-center justify-content-around">
          <div className="col-md-5 pt-4 pb-4">
            <div
              style={{ borderStyle: "dashed", padding: "50px" }}
              className="Card  d-flex flex-column text-center align-items-center"
            >
              <img src={vector} className="imgvec" alt="" />
              <input
                type="file"
                name="uploadimage"
                className="custom-file-input"
                onChange={setFileHandler}
              />
              <span className="upload">Upload File</span>
            </div>
          </div>
          <div className="col-md-5">
            <div className="note">
              <p>Add an Image of 45px X 45px dimension for best visibility</p>
            </div>
            <div className="input-category-name">
              <input
                type="text"
                placeholder={add}
                value={name}
                onChange={setName}
                className="category"
                required
              />
              {des ? (
                <input
                  type="text"
                  placeholder={des}
                  value={descrp}
                  onChange={setDescription}
                  className="category"
                  required
                />
              ) : (
                ""
              )}
              {shortdes ? (
                <input
                  type="text"
                  placeholder={shortdes}
                  value={descrp}
                  onChange={setDescription}
                  className="category"
                  required
                />
              ) : (
                ""
              )}
              {price ? (
                <input
                  type="text"
                  placeholder={price}
                  value={descrp}
                  onChange={setDescription}
                  className="category"
                  required
                />
              ) : (
                ""
              )}

              {cat ? (
                <select className="form-control">
                  {console.log(Categories)}
                </select>
              ) : (
                ""
              )}

              {subcat ? (
                <input
                  type="text"
                  placeholder={price}
                  value={descrp}
                  onChange={setDescription}
                  className="category"
                  required
                />
              ) : (
                ""
              )}
            </div>
            <div className="add-category">
              <button type="submit">{pname}</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Upload;
