import React, { useState, useContext, useEffect } from "react";

import Categorycard from "../Components/Categorycard";
import Upload from "../Components/Upload";
import { AppContext } from "../ContextAPI/Context";

//APIS
import { getAllCategory, deleteCategory } from "../API/Category";

const Category = () => {
  const context = useContext(AppContext);

  const [formdata, setFormData] = useState({
    name: "",
    description: "",
    file: "",
  });

  const [Categories, setCategories] = useState(context.Categories);
  const [Status, setStatus] = useState(false);

  const setNameValue = (e) => {
    setFormData({ ...formdata, name: e.target.value });
  };

  const setDescriptionValue = (e) => {
    setFormData({ ...formdata, description: e.target.value });
  };

  const setFileHandler = (e) => {
    setFormData({ ...formdata, file: e.target.files[0] });
  };

  console.log(context.Status);

  const onDelete = (key) => {
    deleteCategory(key).then((response) => {
      if (response.err) {
        alert("Delete Unsuccessfull!");
        context.setStatus(!context.Status);
      } else {
        alert("Deleted Successfully");
      }
    });
  };

  const handleForm = (e) => {
    e.preventDefault();
    alert("Form Submitted");
  };

  useEffect(() => {
    setCategories(context.Categories);
  }, [context.Status]);

  return (
    <div>
      <h1 className="bold-text">Add category</h1>
      <div className="add-category">
        <Upload
          add="Add Category"
          name={formdata.name}
          descrp={formdata.description}
          des="Add Description"
          setName={setNameValue}
          setDescription={setDescriptionValue}
          setFileHandler={setFileHandler}
          pname="Add Category"
          handleForm={handleForm}
        />
      </div>
      <div className="modify-delete" style={{ marginTop: "120px" }}>
        <h1 className="bold-text">MODIFY/DELETE CATEGORY</h1>
        <div className="row g-4 text-center">
          {Categories
            ? Categories.map((category, index) => (
                <div className="col-md-2" key={index}>
                  <Categorycard
                    categoryid={category.cid}
                    onDelete={onDelete}
                    name={category.name}
                    img={category.url}
                  />
                </div>
              ))
            : ""}
        </div>
      </div>
    </div>
  );
};

export default Category;
