import React, { useState } from "react";

//image
import vector from "../Assets/Images/Vector.png";

const Category = () => {
  const [name, setName] = useState("");

  const [file, setFile] = useState(null);

  const setValue = (e) => {
    setName(e.target.value);
  };
  const setFileHandler = (e) => {
    setFile(e.target.files[0]);
  };

  const handleForm = (e) => {
    e.preventDefault();
    alert("Form Submitted");
  };

  console.log(file, name);

  return (
    <div>
      <h1 className="bold-text">Add category</h1>
      <div className="add-category">
        <form action="" onSubmit={handleForm}>
          <div className="row align-items-center justify-content-around">
            <div className="col-md-5 pt-4 pb-4">
              <div
                style={{ borderStyle: "dashed", padding: "45px" }}
                className="Card  d-flex flex-column text-center align-items-center"
              >
                <img src={vector} className="imgvec" alt="" />
                <input
                  type="file"
                  name="uploadimage"
                  className="custom-file-input"
                  onChange={setFileHandler}
                />
                <span className="upload">Upload File</span>
              </div>
            </div>
            <div className="col-md-5">
              <div className="note">
                <p>Add an Image of 45px X 45px dimension for best visibility</p>
              </div>
              <div className="input-category-name">
                <input
                  type="text"
                  placeholder="Category Name"
                  onChange={setValue}
                  className="category"
                  name="categoryname"
                />
              </div>
              <div className="add-category">
                <button>Add Category</button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Category;
