import React, { useState } from "react";

const Category = () => {
  const [name, setName] = useState("");

  const [file, setFile] = useState(null);

  const setValue = (e) => {
    setName(e.target.value);
  };
  const setFileHandler = (e) => {
    const file = X;

    setFile(e.target.files[0]);
  };

  const handleForm = (e) => {
    e.preventDefault();
    alert("Form Submitted");
  };

  return (
    <div>
      <h1 className="bold-text">Add category</h1>
      <div className="add-category">
        <form action="" onSubmit={handleForm}>
          <div className="row">
            <div className="col-md-6">
              <div className="Card">
                <input
                  type="file"
                  name="uploadimage"
                  onChange={setFileHandler}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="note">
                <p>Add an Image of 45px X 45px dimension for best visibility</p>
              </div>
              <div className="input-category-name">
                <input type="text" onChange={setValue} name="categoryname" />
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Category;
