import React, { useEffect, useState, useContext } from "react";

//API
import { getOrders } from "../API/Order";

import Ordercard from "../Components/Ordercard";
import { ADD_ORDERS } from "../Context/action.types";
import { AppContext } from "../Context/Context";

const Orders = () => {
  const { state, dispatch } = useContext(AppContext);

  useEffect(() => {
    getOrders().then((response) => {
      if (response.err) {
        return alert("Orders cannot be fetched");
      } else {
        dispatch({ type: ADD_ORDERS, payload: response });
      }
    });
  }, []);

  return (
    <div className="order mb-4 d-flex flex-column">
      <h1 className="bold-text">view all orders</h1>
      {!state.Orders.orders
        ? ""
        : state.Orders.orders.map((order) => (
            <Ordercard
              orderid={order.Order_ID}
              datetime={order.Date}
              buyername={order.Buyers_name}
              purchasedworth={order.Total_price}
              orders={order.Total_price}
            />
          ))}
    </div>
  );
};

export default Orders;
