import React, { useState, useContext } from "react";

//image
import bgimg from "../Assets/Images/bgimage.png";
import Categorycard from "../Components/Categorycard";
import Upload from "../Components/Upload";
import { AppContext } from "../ContextAPI/Context";

const Category = () => {
  const context = useContext(AppContext);

  const [name, setName] = useState("");
  const [Description, setDescription] = useState("");
  const [Categories, setCategories] = useState([]);

  const [file, setFile] = useState(null);

  const setNameValue = (e) => {
    setName(e.target.value);
  };

  const setDescriptionValue = (e) => {
    setDescription(e.target.value);
  };

  const setFileHandler = (e) => {
    setFile(e.target.files[0]);
  };

  const handleForm = (e) => {
    e.preventDefault();
    alert("Form Submitted");
  };

  return (
    <div>
      <h1 className="bold-text">Add category</h1>
      <div className="add-category">
        <Upload
          add="Add Category"
          des="Add Description"
          setName={setNameValue}
          setDescription={setDescriptionValue}
          setFileHandler={setFileHandler}
          pname="Add Category"
          handleForm={handleForm}
        />
      </div>
      <div className="modify-delete" style={{ marginTop: "120px" }}>
        <h1 className="bold-text">MODIFY/DELETE CATEGORY</h1>
        <div className="row g-4 text-center">
          <div className="col-md-2">
            <Categorycard img={bgimg} name="KIDNEY CARE" />
          </div>
          <div className="col-md-2">
